mapper.update:
UPDATE tbl_board
   SET bHit = bHit + 1 
 WHERE bNO=#{bNo}

mapper.insert:
insert into tbl_board(
 bTitle, bContent, email, isNotice, isSecret
) values (
#{bTitle}, #{bContent}, #{email}, #{isNotice}, #{isSecret}
)

mapper.update:
update tbl_board
set bTitle = #{bTitle},
    bContent = #{bContent},
    isNotice = #{isNotice},
    isSecret = #{isSecret}
where bNo = #{bNo}

mapper.select:
	select count(*)
      from tbl_reply
     where bNo = #{bNo}

mapper.insert:
insert into tbl_reply(
	bNo, rContent
) values (
	#{bNo}, #{rContent}
)

mapper.update:
update tbl_reply
   set rContent = #{rContent}
 where rNo = #{rNo}

UPDATE tbl_board
   SET bReply = 1
 WHERE bNo=#{bNo}

UPDATE tbl_board
   SET bReply = 0
 WHERE bNo=#{bNo}
