mapper.select:

select p.*, (sumPrice/pNum) as pAvg ,(1-(p.pLowest/(sumPrice/Pnum)))*100 as dRate
  from tbl_product p inner join (
					  			  select pNo, sum(currPrice) sumPrice, count(currPrice) pNum
								    from tbl_price_history
									group by pNo
								) sumP
							  on sumP.pNo = p.pNo
 group by pNo
 order by dRate desc
 limit 0, 5;
