mapper.select:

select p.*, format(p.pLowest, 0) rPLowest, (sumPrice/pNum) as pAvg , format( (1-(p.pLowest/(sumPrice/Pnum)))*100 , 2) as dRate
  from tbl_product p inner join (
					  			  select pNo, sum(currPrice) sumPrice, count(currPrice) pNum
								    from tbl_price_history
									group by pNo
								) sumP
							  on sumP.pNo = p.pNo
 group by pNo
 order by dRate desc
 limit 8;
